example.code:
// Declare some example data
double[] inputs =  { 80, 60, 10, 20, 30 };
double[] outputs = { 20, 40, 30, 50, 60 };
  
// Create a new Simple Linear Regression
SimpleLinearRegression linreg = new SimpleLinearRegression();
  
// Perform the linear regression
linreg.Regress(inputs, outputs);
  
// Compute the output for a given input
double y = linreg.Compute(85);

double[,] matrix = 
{
   { 2, -1.0, 5 },
   { 7,  0.5, 9 },
};
 
// column means are equal to (4.5, -0.25, 7.0)
double[] means = Accord.Statistics.Tools.Mean(matrix);

double[,] matrix = 
{
   { 2, -1.0, 5 },
   { 7,  0.5, 9 },
};

// column means are equal to (4.5, -0.25, 7.0)
double[] colMeans = Accord.Statistics.Tools.Mean(matrix, 0);
  
// row means are equal to (2.0, 5.5)
double[] rowMeans = Accord.Statistics.Tools.Mean(matrix, 1);

double[][] matrix = 
{
    new double[] { 2, -1.0, 5 },
    new double[] { 7,  0.5, 9 },
};
 
// column means are equal to (4.5, -0.25, 7.0)
double[] means = Accord.Statistics.Tools.Mean(matrix);

double[][] matrix = 
{
    new double[] { 2, -1.0, 5 },
    new double[] { 7,  0.5, 9 },
};

// column means are equal to (4.5, -0.25, 7.0)
double[] colMeans = Accord.Statistics.Tools.Mean(matrix, 0);
  
// row means are equal to (2.0, 5.5)
double[] rowMeans = Accord.Statistics.Tools.Mean(matrix, 1);

  // Declare some testing data
  int[][] inputs =
  {
      new int[] { 0,1,1,0 },   // Class 0
      new int[] { 0,0,1,0 },   // Class 0
      new int[] { 0,1,1,1,0 }, // Class 0
      new int[] { 0,1,0 },     // Class 0
      
      new int[] { 1,0,0,1 },   // Class 1
      new int[] { 1,1,0,1 },   // Class 1
      new int[] { 1,0,0,0,1 }, // Class 1
      new int[] { 1,0,1 },     // Class 1
  };
    
  int[] outputs =
  {
      0,0,0,0, // First four sequences are of class 0
      1,1,1,1, // Last  four sequences are of class 1
  };
    
  // We are trying to predict two different classes
  int classes = 2;
    
  // Each sequence may have up to two symbols (0 or 1)
  int symbols = 2;

  // Nested models will have two states each
  int[] states = new int[] { 2, 2 };
   
  // Creates a new Hidden Markov Model Classifier with the given parameters
  MarkovSequenceClassifier hmc = new MarkovSequenceClassifier(classes, symbols, states);
    
  // Will train until convergence of the average likelihood
  double likelihood = hmc.Learn(inputs, outputs, 0.001);
    
  // And finally we will ask for the likelihood of a given sequence
  hmc.Compute(inputs[i], out likelihood);

